// SPDX-License-Identifier: MIT
// FortiFiFortress by FortiFi

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./interfaces/IStrategy.sol";

pragma solidity 0.8.21;

/// @notice Error caused by trying to use recoverERC20 to withdraw strategy receipt tokens
error CantWithdrawStrategyReceipts();

/// @notice Error caused by using 0 address as a parameter
error ZeroAddress();

/// @notice Error caused by trying to deposit 0
error InvalidDeposit();

/// @notice Error caused by trying to withdraw 0
error InvalidWithdrawal();

/// @notice Error thrown when refunding native token fails
error FailedToRefund();

/// @title Base FortiFi Fortress contract
/// @notice Fortresses are vault contracts that are specific to an individual vault receipt. By isolating deposits,
/// Fortresses allow for balance-specific logic from underlying strategies.
contract FortiFiFortress is Ownable {
    using SafeERC20 for IERC20;
    IStrategy public immutable _strat;
    IERC20 public immutable _dToken;
    IERC20 public immutable _wNative;

    constructor(address _strategy, address _depositToken, address _wrappedNative) {
        if (_strategy == address(0)) revert ZeroAddress();
        if (_depositToken == address(0)) revert ZeroAddress();
        if (_wrappedNative == address(0)) revert ZeroAddress();
        _strat = IStrategy(_strategy);
        _dToken = IERC20(_depositToken);
        _wNative = IERC20(_wrappedNative);

        // grant approvals
        _dToken.approve(_strategy, type(uint256).max);
    }

    event DepositMade(uint256 amount, address indexed user);
    event WithdrawalMade(address user);
    event ApprovalsRefreshed();
    event ERC20Recovered(address indexed to, address indexed token, uint256 amount);

    receive() external payable { 
    }

    /// @notice Function to deposit
    function deposit(uint256 _amount, address _user) external virtual onlyOwner returns(uint256 _newStratReceipts){
        if (_amount == 0) revert InvalidDeposit();
        _dToken.safeTransferFrom(msg.sender, address(this), _amount);
        uint256 _beforeBalance = _strat.balanceOf(address(this));

        // deposit to underlying strategy
        _strat.deposit(_amount);

        // calculate new strategy receipt tokens received
        _newStratReceipts = _strat.balanceOf(address(this)) - _beforeBalance;

        // refund left over tokens, if any
        _refund(_user);

        emit DepositMade(_amount, _user);
    }

    /// @notice Function to withdraw everything from vault
    function withdraw(address _user, address[] memory _extraTokens) external virtual onlyOwner {
        uint256 _balance = _strat.balanceOf(address(this));
        if (_balance == 0) revert InvalidWithdrawal();

        _strat.withdraw(_balance);

        // ensure no strategy receipt tokens remain
        _balance = _strat.balanceOf(address(this));
        if (_balance > 0) {
            IERC20(address(_strat)).safeTransfer(_user, _balance);
        }

        // transfer received deposit tokens and refund left over tokens, if any
        _dToken.safeTransfer(msg.sender, _dToken.balanceOf(address(this)));

        // transfer extra reward tokens
        uint256 _length = _extraTokens.length;
        if (_length > 0) {
            for(uint256 i = 0; i < _length; i++) {
                IERC20 _token = IERC20(_extraTokens[i]);
                uint256 _tokenBalance = _token.balanceOf(address(this));
                if (_tokenBalance > 0) {
                    _token.safeTransfer(msg.sender, _tokenBalance);
                }
            }
        }

        _refund(_user);

        emit WithdrawalMade(_user);
    }

    /// @notice Function to return strategy receipts to user when strategy is bricked
    function withdrawBricked(address _user) external virtual onlyOwner {
        uint256 _balance = _strat.balanceOf(address(this));
        if (_balance == 0) revert InvalidWithdrawal();

        // ensure no strategy receipt tokens remain
        _balance = _strat.balanceOf(address(this));
        if (_balance > 0) {
            IERC20(address(_strat)).safeTransfer(_user, _balance);
        }

        emit WithdrawalMade(_user);
    }

    /// @notice Grant max approval to underlying strategy for deposit token
    /// @dev Since Fortresses do not hold deposit tokens for longer than it takes to complete the 
    /// transaction there should be no risk in granting max approval
    function refreshApproval() external {
        _dToken.approve(address(_strat), type(uint256).max);
        emit ApprovalsRefreshed();
    }

    /// @notice Emergency function to recover stuck tokens. 
    function recoverERC20(address _to, address _token, uint256 _amount) external onlyOwner {
        if (_token == address(_strat)) revert CantWithdrawStrategyReceipts();
        IERC20(_token).safeTransfer(_to, _amount);
        emit ERC20Recovered(_to, _token, _amount);
    }

    /// @notice Internal function to refund left over tokens from transactions to user who initiated vault transaction
    function _refund(address _user) internal {
        // Refund left over deposit tokens to strategy, if any
        uint256 _depositTokenBalance = _dToken.balanceOf(address(this));
        if (_depositTokenBalance > 0) {
            _dToken.safeTransfer(msg.sender, _depositTokenBalance);
        }

        // Refund left over wrapped native tokens to user, if any
        uint256 _wrappedNativeTokenBalance = _wNative.balanceOf(address(this));
        if (_wrappedNativeTokenBalance > 0) {
            _wNative.safeTransfer(_user, _wrappedNativeTokenBalance);
        }

        // Refund left over native tokens to user, if any
        if (address(this).balance > 0) {
            (bool success, ) = payable(_user).call{ value: address(this).balance }("");
		    if (!success) revert FailedToRefund();
        }
    }
}

contracts/strategies/FortiFiGLPStrategy..content:
// SPDX-License-Identifier: MIT
// FortiFiGLPStrategy by FortiFi

import "./FortiFiStrategy.sol";
import "./FortiFiFortress.sol";
import "./interfaces/IFortress.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IGLPRewardRouter {
    function glpManager() external view returns(address);
    function mintAndStakeGlp(address _token, uint256 _amount, uint256 _minUsdg, uint256 _minGlp) external returns (uint256);
    function unstakeAndRedeemGlp(address _tokenOut, uint256 _glpAmount, uint256 _minOut, address _receiver) external returns (uint256);
}

interface IGLPManager {
    function getPrice(bool _maximise) external view returns (uint256);
}

pragma solidity 0.8.21;

/// @title GLP FortiFi Strategy contract
/// @notice This contract allows for FortiFi vaults to utilize GLP strategies by minting GLP with USDC and depositing into a 
/// simple vault like Yield Yak
contract FortiFiGLPStrategy is FortiFiStrategy {
    using SafeERC20 for IERC20;

    uint16 public slippageBps = 200;
    uint16 public constant BPS = 10000;

    IGLPRewardRouter public rewardRouter;
    IERC20 public immutable fsGLP;

    constructor(address _strategy, 
        address _depositToken, // MUST BE USDC
        address _wrappedNative,
        address _feeManager,
        address _feeCalculator,
        address _fsglp,
        address _glpRewardRouter) 
        FortiFiStrategy(_strategy, _depositToken, _wrappedNative, _feeManager, _feeCalculator) {
        if (_fsglp == address(0)) revert ZeroAddress();
        if (_glpRewardRouter == address(0)) revert ZeroAddress();
        setGlpRewardRouter(_glpRewardRouter);
        fsGLP = IERC20(_fsglp);
        refreshApprovals();
    }

    event GLPRewardRouterSet(address newManager);
    event ApprovalsRefreshed();
    event SlippageSet(uint16 newSlippage);

    /// @notice Function to deposit
    /// @dev If a user has not deposited previously, this function will deploy a FortiFiVectorFortress contract
    /// instead of the base FortiFiFortress contract
    function depositToFortress(uint256 _amount, address _user, uint256 _tokenId) external override {
        if (_amount == 0) revert InvalidDeposit();
        if (!isFortiFiVault[msg.sender]) revert InvalidCaller();
        if (strategyIsBricked) revert StrategyBricked();
        _dToken.safeTransferFrom(msg.sender, address(this), _amount);
        IFortress _fortress;

        // If user has not deposited previously, deploy Fortress
        if (vaultToTokenToFortress[msg.sender][_tokenId] == address(0)) {
            FortiFiFortress _fort = new FortiFiFortress(_strat, address(fsGLP), address(_wNative));
            _fortress = IFortress(address(_fort));
            vaultToTokenToFortress[msg.sender][_tokenId] = address(_fortress);
            emit FortressCreated(msg.sender, _tokenId, address(_strat));
        } else {
            _fortress = IFortress(vaultToTokenToFortress[msg.sender][_tokenId]);
        }

        // get price and mint GLP
        uint256 _glpPrice = IGLPManager(rewardRouter.glpManager()).getPrice(true); // maximize price
        uint256 _glpOut = _amount * (10**18) / _glpPrice*10**(30 - 6); // GLP decimals are 18, price precision is 30 - 6 (USDC decimals)
        uint256 _glpAmount = rewardRouter.mintAndStakeGlp(address(_dToken), _amount, 0, _glpOut * (BPS - slippageBps) / BPS);

        // approve and deposit
        fsGLP.approve(address(_fortress), _glpAmount);
        uint256 _receipts = _fortress.deposit(_amount, _user);

        // mint receipt tokens = to what was received from Fortress
        _mint(msg.sender, _receipts);

        // Refund left over deposit tokens, if any
        uint256 _depositTokenBalance = _dToken.balanceOf(address(this));
        if (_depositTokenBalance > 0) {
            _dToken.safeTransfer(msg.sender, _depositTokenBalance);
        }

        emit DepositToFortress(msg.sender, _user, address(_strat), _amount);
    }

    /// @notice Function to withdraw
    /// @dev Override is required because Vector Fortresses need slippage passed in to withdrawal function
    function withdrawFromFortress(uint256 _amount, address _user, uint256 _tokenId) external override {
        if (_amount == 0) revert InvalidWithdrawal();
        if (vaultToTokenToFortress[msg.sender][_tokenId] == address(0)) revert NoFortress();

        // burn receipt tokens and withdraw from Fortress
        _burn(msg.sender, _amount);

        if (strategyIsBricked) {
            IFortress(vaultToTokenToFortress[msg.sender][_tokenId]).withdrawBricked(_user);

            emit WithdrawBrickedFromFortress(msg.sender, _user, address(_strat), _tokenId);
        } else {
            IFortress(vaultToTokenToFortress[msg.sender][_tokenId]).withdraw(_user, extraTokens);

            uint256 _fsGlpReceived = fsGLP.balanceOf(address(this));

            // redeem GLP for deposit token
            uint256 _glpPrice = IGLPManager(rewardRouter.glpManager()).getPrice(true); // maximize price
            uint256 _dTokenOut = _amount * (_glpPrice / 10**18) / 10**(30 - 6); // GLP decimals are 18, price precision is 30 - 6 (USDC decimals)
            uint256 _depositTokenReceived = rewardRouter.unstakeAndRedeemGlp(address(_dToken), _fsGlpReceived, _dTokenOut * (BPS - slippageBps) / BPS, msg.sender);

            // Refund left over deposit tokens, if any
            uint256 _depositTokenBalance = _dToken.balanceOf(address(this));
            if (_depositTokenBalance > 0) {
                _dToken.safeTransfer(msg.sender, _depositTokenBalance);
            }

            // handle fees on extra reward tokens
            uint256 _length = extraTokens.length;
            if (_length > 0) {
                for(uint256 i = 0; i < _length; i++) {
                    IERC20 _token = IERC20(extraTokens[i]);
                    uint256 _tokenBalance = _token.balanceOf(address(this));
                    if (_tokenBalance > 0) {
                        uint256 _fee = feeCalc.getFees(_user, _tokenBalance);
                        feeMgr.collectFees(extraTokens[i], _fee);
                        _token.safeTransfer(_user, _tokenBalance - _fee);
                    }
                }
            }

            emit WithdrawFromFortress(msg.sender, _user, address(_strat), _tokenId, _depositTokenReceived);
        }
    }

    /// @notice Function to set max approvals for reward manager. 
    /// @dev Since contract never holds deposit tokens max approvals should not matter. 
    function refreshApprovals() public {
        _dToken.approve(address(rewardRouter), type(uint256).max);

        emit ApprovalsRefreshed();
    }

    /// @notice Function to set the GLP Reward Manager
    function setGlpRewardRouter(address _newManager) public onlyOwner {
        rewardRouter = IGLPRewardRouter(_newManager);
        emit GLPRewardRouterSet(_newManager);
    }

    /// @notice Function to set the slippage if 1% is not sufficient
    function setSlippage(uint16 _amount) external onlyOwner {
        slippageBps = _amount;
        emit SlippageSet(_amount);
    }

}

contracts/strategies/FortiFiStrategy.sol.content:
// SPDX-License-Identifier: MIT
// FortiFiStrategy by FortiFi

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../fee-calculators/interfaces/IFortiFiFeeCalculator.sol";
import "../fee-managers/interfaces/IFortiFiFeeManager.sol";
import "./interfaces/IFortress.sol";
import "./interfaces/IVault.sol";
import "./FortiFiFortress.sol";

pragma solidity 0.8.21;

/// @notice Error when vault does not implement ISAMS or IMASS interface (0xafba703c)
error InvalidVaultImplementation();

/// @notice Error caused when calling address is not a valid vault 
error InvalidCaller();

/// @notice Error caused when trying to withdraw from non-existent fortress
error NoFortress();

/// @notice Error caused when trying to deposit to bricked strategy
error StrategyBricked();

/// @title Base FortiFi Strategy contract
/// @notice This contract should be used when a yield strategy requires special logic beyond
/// simple deposit(amount deposit token) and withdraw(receipt tokens to burn). These strategies
/// are designed to only be called by FortiFi SAMS and MASS Vaults.
contract FortiFiStrategy is Ownable, ERC20 {
    using SafeERC20 for IERC20;
    address public immutable _strat;
    address public immutable _wNative;
    bool public strategyIsBricked;

    IERC20 public immutable _dToken;
    IFortiFiFeeCalculator public feeCalc;
    IFortiFiFeeManager public feeMgr;

    address[] public extraTokens;
    
    mapping(address => bool) public isFortiFiVault;
    mapping(address => mapping(uint256 => address)) public vaultToTokenToFortress;

    event FortressCreated(address indexed vault, uint256 tokenId, address indexed strategy);
    event DepositToFortress(address indexed vault, address indexed user, address indexed strategy, uint256 amountDeposited);
    event WithdrawFromFortress(address indexed vault, address indexed user, address indexed strategy, uint256 tokenId, uint256 amountReceived);
    event WithdrawBrickedFromFortress(address indexed vault, address indexed user, address indexed strategy, uint256 tokenId);
    event VaultSet(address vault, bool approved);
    event ExtraTokensSet(address[] tokens);
    event SetStrategyAsBricked(bool isBricked);
    event ERC20Recovered(address indexed token, uint256 amount);
    event ERC20RecoveredFromFortress(address indexed fortress, address indexed token, uint256 amount);
    event FeeManagerSet(address feeManager);
    event FeeCalculatorSet(address feeCalculator);

    constructor(address _strategy, 
        address _depositToken, 
        address _wrappedNative,
        address _feeManager,
        address _feeCalculator) ERC20("FortiFi Strategy Receipt", "FFSR") {
        if (_strategy == address(0)) revert ZeroAddress();
        if (_depositToken == address(0)) revert ZeroAddress();
        if (_wrappedNative == address(0)) revert ZeroAddress();
        if (_feeManager == address(0)) revert ZeroAddress();
        if (_feeCalculator == address(0)) revert ZeroAddress();
        _strat = _strategy;
        _wNative = _wrappedNative;
        _dToken = IERC20(_depositToken);
        feeCalc = IFortiFiFeeCalculator(_feeCalculator);
        feeMgr = IFortiFiFeeManager(_feeManager);
    }

    /// @notice Function to deposit
    /// @dev If a user has not deposited previously, this function will deploy a FortiFiFortress contract
    /// to interact with the underlying strategy for a specific vault receipt token. This allows user deposits to be isolated
    /// as many strategies utilize special logic that is dependent on the balance of the address interacting with them.
    function depositToFortress(uint256 _amount, address _user, uint256 _tokenId) external virtual {
        if (_amount == 0) revert InvalidDeposit();
        if (!isFortiFiVault[msg.sender]) revert InvalidCaller();
        if (strategyIsBricked) revert StrategyBricked();
        _dToken.safeTransferFrom(msg.sender, address(this), _amount);
        IFortress _fortress;

        // If user has not deposited previously, deploy Fortress
        if (vaultToTokenToFortress[msg.sender][_tokenId] == address(0)) {
            FortiFiFortress _fort = new FortiFiFortress(_strat, address(_dToken), address(_wNative));
            _fortress = IFortress(address(_fort));
            vaultToTokenToFortress[msg.sender][_tokenId] = address(_fortress);
            emit FortressCreated(msg.sender, _tokenId, address(_strat));
        } else {
            _fortress = IFortress(vaultToTokenToFortress[msg.sender][_tokenId]);
        }

        // approve and deposit
        _dToken.approve(address(_fortress), _amount);
        uint256 _receipts = _fortress.deposit(_amount, _user);

        // mint receipt tokens equal to what was received from Fortress
        _mint(msg.sender, _receipts);

        // Refund left over deposit tokens, if any
        uint256 _depositTokenBalance = _dToken.balanceOf(address(this));
        if (_depositTokenBalance > 0) {
            _dToken.safeTransfer(msg.sender, _depositTokenBalance);
        }

        emit DepositToFortress(msg.sender, _user, address(_strat), _amount);
    }

    /// @notice Function to withdraw
    function withdrawFromFortress(uint256 _amount, address _user, uint256 _tokenId) external virtual {
        if (_amount == 0) revert InvalidWithdrawal();
        if (vaultToTokenToFortress[msg.sender][_tokenId] == address(0)) revert NoFortress();

        // burn receipt tokens and withdraw from Fortress
        _burn(msg.sender, _amount);

        if(strategyIsBricked) {
            IFortress(vaultToTokenToFortress[msg.sender][_tokenId]).withdrawBricked(_user);

            emit WithdrawBrickedFromFortress(msg.sender, _user, address(_strat), _tokenId);
        } else {
            IFortress(vaultToTokenToFortress[msg.sender][_tokenId]).withdraw(_user, extraTokens);

            uint256 _depositTokenReceived = _dToken.balanceOf(address(this));

            // transfer received deposit tokens
            _dToken.safeTransfer(msg.sender, _depositTokenReceived);

            // handle fees on extra reward tokens
            uint256 _length = extraTokens.length;
            if (_length > 0) {
                for(uint256 i = 0; i < _length; i++) {
                    IERC20 _token = IERC20(extraTokens[i]);
                    uint256 _tokenBalance = _token.balanceOf(address(this));
                    if (_tokenBalance > 0) {
                        uint256 _fee = feeCalc.getFees(_user, _tokenBalance);
                        feeMgr.collectFees(extraTokens[i], _fee);
                        _token.safeTransfer(_user, _tokenBalance - _fee);
                    }
                }
            }

            emit WithdrawFromFortress(msg.sender, _user, address(_strat), _tokenId, _depositTokenReceived);
        }
    }

    /// @notice Set valid vaults
    function setVault(address _vault, bool _approved) external onlyOwner {
        if (!IVault(_vault).supportsInterface(0xafba703c)) revert InvalidVaultImplementation();
        isFortiFiVault[_vault] = _approved;
        emit VaultSet(_vault, _approved);
    }

    /// @notice Set extra reward tokens for strategy
    function setExtraTokens(address[] calldata _tokens) external onlyOwner {
        extraTokens = _tokens;
        emit ExtraTokensSet(_tokens);
    }

    /// @notice Set strategy as bricked
    function setStrategyBricked(bool _isBricked) external onlyOwner {
        strategyIsBricked = _isBricked;
        emit SetStrategyAsBricked(_isBricked);
    }

    /// @notice Function to set new FortiFiFeeManager contract
    function setFeeManager(address _contract) external onlyOwner {
        if (_contract == address(0)) revert ZeroAddress();
        feeMgr = IFortiFiFeeManager(_contract);
        emit FeeManagerSet(_contract);
    }

    /// @notice Function to set new FortiFiFeeCalculator contract
    function setFeeCalculator(address _contract) external onlyOwner {
        if (_contract == address(0)) revert ZeroAddress();
        feeCalc = IFortiFiFeeCalculator(_contract);
        emit FeeCalculatorSet(_contract);
    }

    /// @notice Emergency function to recover stuck tokens
    function recoverERC20(address _token, uint256 _amount) external onlyOwner {
        IERC20(_token).safeTransfer(msg.sender, _amount);
        emit ERC20Recovered(_token, _amount);
    }

    /// @notice Emergency function to recover stuck tokens from Fortress
    function recoverFromFortress(address _fortress, address _token, uint256 _amount) external onlyOwner {
        IFortress(_fortress).recoverERC20(msg.sender, _token, _amount);
        emit ERC20RecoveredFromFortress(_fortress, _token, _amount);
    }

}

