// FortiFiFortress by FortiFi

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/IStrategy.sol";

pragma solidity ^0.8.18;

/// @title Base FortiFi Fortress contract
/// @notice Fortresses are vault contracts that are specific to an individual vault receipt. By isolating deposits,
/// Fortresses allow for balance-specific logic from underlying strategies.
contract FortiFiFortress is Ownable {
    IStrategy internal immutable _strat;
    IERC20 internal immutable _dToken;
    IERC20 internal immutable _wNative;

    constructor(address _strategy, address _depositToken, address _wrappedNative, address _fortiFiStrat) {
        require(_strategy != address(0), "FortiFi: Invalid strategy");
        require(_depositToken != address(0), "FortiFi: Invalid deposit token");
        require(_wrappedNative != address(0), "FortiFi: Invalid native token");
        require(_fortiFiStrat != address(0), "FortiFi: Invalid owner");
        _strat = IStrategy(_strategy);
        _dToken = IERC20(_depositToken);
        _wNative = IERC20(_wrappedNative);

        // grant approvals
        _dToken.approve(_strategy, type(uint256).max);

        // owner is the FortiFiStrategy contract that creates this Fortress
        _transferOwnership(_fortiFiStrat);
    }

    receive() external payable { 
    }

    /// @notice Function to deposit
    function deposit(uint256 _amount, address _user) external virtual onlyOwner returns(uint256 _newStratReceipts){
        require(_amount > 0, "FortiFi: 0 deposit");
        require(_dToken.transferFrom(msg.sender, address(this), _amount), "FortiFi: Failed to transfer dep.");
        uint256 _beforeBalance = _strat.balanceOf(address(this));

        // deposit to underlying strategy
        _strat.deposit(_amount);

        // calculate new strategy receipt tokens received
        _newStratReceipts = _strat.balanceOf(address(this)) - _beforeBalance;

        // refund left over tokens, if any
        _refund(_user);
    }

    /// @notice Function to withdraw everything from vault
    function withdraw(address _user) external virtual onlyOwner {
        uint256 _balance = _strat.balanceOf(address(this));
        require(_balance > 0, "FortiFi: 0 withdraw");

        _strat.withdraw(_balance);

        // ensure no strategy receipt tokens remain
        _balance = _strat.balanceOf(address(this));
        if (_balance > 0) {
            require(_strat.transfer(_user, _balance));
        }

        // transfer received deposit tokens and refund left over tokens, if any
        require(_dToken.transfer(msg.sender, _dToken.balanceOf(address(this))), "FortiFi: Failed to transfer dep.");
        _refund(_user);
    }

    /// @notice Grant max approval to underlying strategy for deposit token
    /// @dev Since Fortresses do not hold deposit tokens for longer than it takes to complete the 
    /// transaction there should be no risk in granting max approval
    function refreshApproval() external {
        _dToken.approve(address(_strat), type(uint256).max);
    }

    /// @notice View function returns specified wrapped native token address
    function wrappedNativeToken() external view returns(address) {
        return address(_wNative);
    }

    /// @notice View function returns specified deposit token address
    function depositToken() external view returns(address) {
        return address(_dToken);
    }

    /// @notice View function returns specified underlying strategy address
    function strategy() external view returns(address) {
        return address(_strat);
    }

    /// @notice Emergency function to recover stuck tokens. 
    function recoverERC20(address _to, address _token, uint256 _amount) external onlyOwner {
        IERC20(_token).transfer(_to, _amount);
    }

    /// @notice Internal function to refund left over tokens from transactions to user who initiated vault transaction
    function _refund(address _user) internal {
        // Refund left over deposit tokens to strategy, if any
        uint256 _depositTokenBalance = _dToken.balanceOf(address(this));
        if (_depositTokenBalance > 0) {
            require(_dToken.transfer(msg.sender, _depositTokenBalance), "FortiFi: Failed to refund ERC20");
        }

        // Refund left over wrapped native tokens to user, if any
        uint256 _wrappedNativeTokenBalance = _wNative.balanceOf(address(this));
        if (_wrappedNativeTokenBalance > 0) {
            require(_wNative.transfer(_user, _wrappedNativeTokenBalance), "FortiFi: Failed to refund native");
        }

        // Refund left over native tokens to user, if any
        if (address(this).balance > 0) {
            (bool success, ) = payable(_user).call{ value: address(this).balance }("");
		    require(success, "FortiFi: Failed to refund native");
        }
    }
}","keccak256":"0x512da4079fb30c3091992d2ab86697d1d95a52f18e6d9d2b4fab2e04de9ec797","license":"MIT"},"contracts/strategies/FortiFiStrategy.sol":{"content":"// SPDX-License-Identifier: MIT
// FortiFiStrategy by FortiFi

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./interfaces/IFortress.sol";
import "./FortiFiFortress.sol";

pragma solidity ^0.8.18;

/// @title Base FortiFi Strategy contract
/// @notice This contract should be used when a yield strategy requires special logic beyond
/// simple deposit(amount deposit token) and withdraw(receipt tokens to burn). These strategies
/// are designed to only be called by FortiFi SAMS and MASS Vaults.
contract FortiFiStrategy is Ownable, ERC20 {
    address internal immutable _strat;
    address internal immutable _wNative;
    IERC20 internal immutable _dToken;
    

    mapping(address => bool) public isFortiFiVault;
    mapping(address => mapping(uint256 => address)) public vaultToTokenToFortress;

    event FortressCreated(address indexed vault, uint256 tokenId, address indexed strategy);
    event DepositToFortress(address indexed vault, address indexed user, address indexed strategy, uint256 amountDeposited);
    event WithdrawFromFortress(address indexed vault, address indexed user, address indexed strategy, uint256 amountReceived);

    constructor(address _strategy, address _depositToken, address _wrappedNative) ERC20("FortiFi Strategy Receipt", "FFSR") {
        require(_strategy != address(0), "FortiFi: Invalid strategy");
        require(_depositToken != address(0), "FortiFi: Invalid deposit token");
        require(_wrappedNative != address(0), "FortiFi: Invalid native token");
        _strat = _strategy;
        _wNative = _wrappedNative;
        _dToken = IERC20(_depositToken);
    }

    /// @notice Function to deposit
    /// @dev If a user has not deposited previously, this function will deploy a FortiFiFortress contract
    /// to interact with the underlying strategy for a specific vault receipt token. This allows user deposits to be isolated
    /// as many strategies utilize special logic that is dependent on the balance of the address interacting with them.
    function depositToFortress(uint256 _amount, address _user, uint256 _tokenId) external virtual {
        require(_amount > 0, "FortiFi: 0 deposit");
        require(isFortiFiVault[msg.sender], "FortiFi: Invalid vault");
        require(_dToken.transferFrom(msg.sender, address(this), _amount), "FortiFi: Failed to transfer dep.");
        IFortress _fortress;

        // If user has not deposited previously, deploy Fortress
        if (vaultToTokenToFortress[msg.sender][_tokenId] == address(0)) {
            FortiFiFortress _fort = new FortiFiFortress(_strat, address(_dToken), address(_wNative), address(this));
            _fortress = IFortress(address(_fort));
            vaultToTokenToFortress[msg.sender][_tokenId] = address(_fortress);
            emit FortressCreated(msg.sender, _tokenId, address(_strat));
        } else {
            _fortress = IFortress(vaultToTokenToFortress[msg.sender][_tokenId]);
        }

        // approve and deposit
        _dToken.approve(address(_fortress), _amount);
        uint256 _receipts = _fortress.deposit(_amount, _user);

        // mint receipt tokens equal to what was received from Fortress
        _mint(msg.sender, _receipts);

        // Refund left over deposit tokens, if any
        uint256 _depositTokenBalance = _dToken.balanceOf(address(this));
        if (_depositTokenBalance > 0) {
            require(_dToken.transfer(msg.sender, _depositTokenBalance), "FortiFi: Failed to refund ERC20");
        }

        emit DepositToFortress(msg.sender, _user, address(_strat), _amount);
    }

    /// @notice Function to withdraw
    function withdrawFromFortress(uint256 _amount, address _user, uint256 _tokenId) external virtual {
        require(_amount > 0, "FortiFi: 0 withdraw");
        require(vaultToTokenToFortress[msg.sender][_tokenId] != address(0), "FortiFi: No fortress");

        // burn receipt tokens and withdraw from Fortress
        _burn(msg.sender, _amount);
        IFortress(vaultToTokenToFortress[msg.sender][_tokenId]).withdraw(_user);

        uint256 _depositTokenReceived = _dToken.balanceOf(address(this));

        // transfer received deposit tokens
        require(_dToken.transfer(msg.sender, _depositTokenReceived), "FortiFi: Failed to transfer dep.");

        emit WithdrawFromFortress(msg.sender, _user, address(_strat), _depositTokenReceived);
    }

    /// @notice Set valid vaults
    function setVault(address _strategy, bool _approved) external onlyOwner {
        isFortiFiVault[_strategy] = _approved;
    }

    /// @notice Emergency function to recover stuck tokens
    function recoverERC20(address _token, uint256 _amount) external onlyOwner {
        IERC20(_token).transfer(msg.sender, _amount);
    }

    /// @notice Emergency function to recover stuck tokens from Fortress
    function recoverFromFortress(address _fortress, address _token, uint256 _amount) external onlyOwner {
        IFortress(_fortress).recoverERC20(msg.sender, _token, _amount);
    }

    /// @notice View function to return specified wrapped native token address
    function wrappedNativeToken() external view returns(address) {
        return _wNative;
    }

    /// @notice View function to return specified deposit token address
    function depositToken() external view returns(address) {
        return address(_dToken);
    }

    /// @notice View function to return specified underlying strategy address
    function strategy() external view returns(address) {
        return _strat;
    }

}","keccak256":"0xbc6af5e1421fad976486fffc68388bc3d372338bc3cdc7efbcb205f82dd296c9","license":"MIT"},"contracts/strategies/FortiFiVectorFortress.sol":{"content":"// SPDX-License-Identifier: MIT
// FortiFiVectorFortress by FortiFi

import "./FortiFiFortress.sol";
import "./interfaces/IVectorStrategy.sol";

pragma solidity ^0.8.18;

/// @title FortiFi Fortress contract for Vector Finance strategies
/// @notice This Fortress contract is specifically made to interact with Vector Finance strategies
contract FortiFiVectorFortress is FortiFiFortress {
    uint16 private constant BPS = 10_000;
    IVectorStrategy private immutable _vectorStrat;

    constructor(address _strategy, address _depositToken, address _wrappedNative, address _fortiFiStrat) 
        FortiFiFortress(_strategy, _depositToken, _wrappedNative, _fortiFiStrat) {
        _vectorStrat = IVectorStrategy(_strategy);
    }

    /// @notice Nullified withdraw function
    /// @dev this override is to ensure an incorrect withdraw call is not made from the strategy contract.
    /// Vector strategies require calling withdrawVector(_amount, _slippageBps)
    function withdraw(address) external override onlyOwner {
        revert("FortiFi: Invalid withdraw");
    }

    /// @notice Function to withdraw
    /// @dev Vector Finance strategies require that you pass in the amount of deposit tokens you expect to receive
    /// rather than the amount of receipt tokens you want to burn as well as a minAmount. This is calculated by utilizing the
    /// getDepositTokensForShares view function and applying a slippage amount (typically 1%).
    function withdrawVector(address _user, uint256 _slippageBps) external onlyOwner {
        uint256 _balance = _vectorStrat.balanceOf(address(this));
        require(_balance > 0, "FortiFi: 0 withdraw");

        // calculate _tokensForShares and apply slippage
        uint256 _tokensForShares = _vectorStrat.getDepositTokensForShares(_balance);
        uint256 _minAmount = _tokensForShares * (BPS - _slippageBps) / BPS;
        
        // withdraw from vector strategy
        _vectorStrat.withdraw(_tokensForShares, _minAmount);
        
        // ensure no strategy receipt tokens remain
        _balance = _vectorStrat.balanceOf(address(this));
        if (_balance > 0) {
            require(_vectorStrat.transfer(_user, _balance));
        }

        // transfer received deposit tokens and refund left over tokens, if any
        require(_dToken.transfer(msg.sender, _dToken.balanceOf(address(this))), "FortiFi: Failed to transfer dep.");
        _refund(_user);
    }
    
}","keccak256":"0x069eeb3b030ba61ebb87e48fcb2ec1bff0a6452e9eeb6bffbce2618f906ff868","license":"MIT"},"contracts/strategies/FortiFiVectorStrategy.sol":{"content":"// SPDX-License-Identifier: MIT
